import { useState, useEffect } from 'react';

const Books = (props) => {
    const { currentPage, goNextPage, goPreviousPage, pageCalculation } = props;

    const [isDisable, setisDisable] = useState(false);

    useEffect(() => {
        const prevBtnDisable = () => {
            (currentPage === 1) ? setisDisable(true) : setisDisable(false);
        }
        const nextBtnDisable = () => {
            (currentPage === 10) ? setisDisable(true) : setisDisable(false);
        }
        prevBtnDisable();
        // nextBtnDisable();    
    }, [currentPage])

    return (
        <>
            <div className='selectRange'>
                <button>{("")}</button>
            </div>
            <div className="Recipes">
                <table >
                    <thead>
                        <tr>
                            <th>Book name</th>
                            <th>Author name</th>
                            <th>Price</th>
                            <th>Rating</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pageCalculation()}
                    </tbody>
                </table>
                <div className='pagination-buttons'>
                    <button className='pervious-button' disabled={isDisable} onClick={goPreviousPage}>Previous</button>
                    <span className='current-page'>{currentPage}</span>
                    <button className='next-button'  onClick={goNextPage}>Next</button>
                </div>
            </div>

        </>
    );
}

export default Books
