import { useState, useEffect } from 'react';

const Books = (props) => {
    const { currentPage, goNextPage, goPreviousPage, pageCalculation, changePerPage } = props;

    const [disable, setisDisable] = useState({
        isPrevDisable: false,
        isNextDisable: false
    });

    useEffect(() => {
        const prevBtnDisable = () => {
            (currentPage === 1) ? setisDisable({isPrevDisable: true}) : setisDisable({isPrevDisable: false});
        }
        const nextBtnDisable = () => {
            (currentPage === 10) ? setisDisable({isNextDisable: true}) : setisDisable({isNextDisable: false});
        }
        prevBtnDisable();
        // nextBtnDisable();    
    }, [currentPage])

    return (
        <>
            <div className='selectRange'>
                <button onClick={() => changePerPage(10)} className='perPageButton' >10</button>
                <button onClick={() => changePerPage(20)} className='perPageButton' >20</button>
                <button onClick={() => changePerPage(25)} className='perPageButton' >25</button>
            </div>
            <div className="Recipes">
                <table >
                    <thead>
                        <tr>
                            <th>Book name</th>
                            <th>Author name</th>
                            <th>Price</th>
                            <th>Rating</th>
                        </tr>
                    </thead>
                    <tbody>
                        {pageCalculation()}
                    </tbody>
                </table>
                <div className='pagination-buttons'>
                    <button className='pervious-button' disabled={isDisable} onClick={goPreviousPage}>Previous</button>
                    <span className='current-page'>{currentPage}</span>
                    <button className='next-button'  onClick={goNextPage}>Next</button>
                </div>
            </div>

        </>
    );
}

export default Books
